import React, { useEffect, useState, Suspense } from "react";
import { HashRouter, Router, Route, Link, Routes } from "react-router-dom";

import LoadingBar from "react-top-loading-bar";

/////page ////
import Rute from "./root";
export const DelayedFallback = ({ children, delay = 300 }) => {
	const [show, setShow] = useState(false);
	useEffect(() => {
		let timeout = setTimeout(() => setShow(true), delay);
		return () => {
			clearTimeout(timeout);
		};
	}, []);
	return <>{show && children}</>;
};

export default function Rooter() {
	const [loading, setLoading] = useState(true);
	return (
		<Routes>
			{Rute.map(({ path, Component }, i) => (
				<Route
					key={i}
					path={path}
					element={
						<Suspense fallback={DelayedFallback}>
							<h2>ok</h2>
							{/* <Component /> */}
							{/* <Website components={<Component {...props} />}></Website> */}
						</Suspense>
					}
				></Route>
			))}
		</Routes>
	);
}
